YUI.add("rednose-formatter",function(e,t){function n(){n.superclass.constructor.apply(this)}n.size=function(e){e=parseFloat(e);for(var t=0;t<4;t++)if(e<1e3){e=Math.round(e*100)/100;switch(t){case 0:e=e.toString()+" bytes";break;case 1:e=e.toString()+" KB";break;case 2:e=e.toString()+" MB";break;case 3:e=e.toString()+" GB"}t=4}else e/=1e3;return e},e.namespace("Rednose").Formatter=n},"1.4.0");
