YUI.add("rednose-util",function(e,t){function r(){}var n="\u2026";r.formatLabel=function(e){return e.length>24?e.substr(0,12)+n+e.substr(e.length-12,e.length):e},r.formatDateTime=function(t){return e.Date.format(t,{format:"%x %R"})},r.isAncestor=function(e,t){return t.ancestor("#"+e.get("id"))},r.capitalizeFirstLetter=function(e){return e.charAt(0).toUpperCase()+e.slice(1)},r.camelCaseToDash=function(e){return e.replace(/([A-Z])/g,function(e){return"-"+e.toLowerCase()})},e.namespace("Rednose").Util=r},"1.6.0",{requires:["datatype-date"]});
